import React, { useEffect, useState } from 'react';
import { getTasks } from '../services/task.service';
import { getUsers } from '../services/user.service';
import Task from '../types/Task';
import User from '../types/User';

interface Props {
    userId: string;
}

const UserTaskList: React.FC<Props> = ({ userId }) => {
    const [user, setUser] = useState<User | null>(null);
    const [tasks, setTasks] = useState<Task[]>([]);
    const [expanded, setExpanded] = useState(false);

    useEffect(() => {
        const fetchUser = async () => {
            const users = await getUsers();
            const currentUser = users.find((user: { id: string; }) => user.id === userId);
            setUser(currentUser);
        };
        fetchUser();
    }, [userId]);

    useEffect(() => {
        const fetchTasks = async () => {
            const allTasks = await getTasks();
            const userTasks = allTasks.filter((task: { userId: string; }) => task.userId === userId);
            setTasks(userTasks);
        };
        fetchTasks();
    }, [userId]);

    const toggleList = () => {
        setExpanded(!expanded);
    };

    return (
        <div className="userTask">
            <div className="list-header" onClick={toggleList}>
                {tasks.length === 0 ? (
                    <p>Aucune tâche associée</p>
                ) : (
                    <p>{tasks.length} tâche{tasks.length > 1 ? "s" : ""} associée{tasks.length > 1 ? "s" : ""}</p>
                )}
                <span>{expanded ? "-" : "+"}</span>
            </div>
            {expanded && (
                <ul className="mat-list">
                    {tasks.map((task) => (
                        <li key={task._id} className="mat-list-item">
                            {task.title}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default UserTaskList;
