import { FC, useState, useEffect } from 'react';
import User from "../types/User";
import { getUsers, addUser } from '../services/user.service';
import Modal from './Modal';
import UserForm from './UserForm';
import { IoEyeSharp, IoAddCircle } from 'react-icons/io5';
import { getTasks } from '../services/task.service';

interface ListUserProps {
  title: string;
}

const ListUsers: FC<ListUserProps> = ({ title }: ListUserProps) => {
  const [users, setUsers] = useState<User[] | null>([]);
  const [isOpenForm, setOpenForm] = useState(false);
  const [refresh, setRefresh] = useState(0);

  useEffect(() => {
    const getData = async () => {
      const users: User[] = await getUsers();
      setUsers(users);
    };
    getData();
  }, [refresh]);

  const sendNewUser = async (formData: User) => {
    await addUser(formData);
    setRefresh(refresh + 1);
    setOpenForm(false);
  };

  const selectTask = async (userId: number) => {
    const tasks = await getTasks();
    const selectedTask = prompt(`Choose a task for user ${userId}`, '');
    if (selectedTask) {
      console.log(`User ${userId} selected task ${selectedTask}`);
      // Perform any other action with the selected task here
    }
  };

  return (
      <div>
        <h3>{title}</h3>
        <div>
          <button onClick={() => setOpenForm(true)}>Ajouter un utilisateur</button>
          <Modal
              isOpen={isOpenForm}
              onClose={() => setOpenForm(false)}
              title="Enregistrer un utilisateur"
              content={<UserForm onSubmit={sendNewUser} />}
          />
        </div>
        <table className='center'>
          <thead>
          <tr>
            <th>Nom</th>
            <th>Email</th>
            <th>Gestion des tâches</th>
          </tr>
          </thead>
          <tbody>
          {users?.map((user, index) => (
              <tr key={index}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <button className='iconButton' onClick={() => alert(`Gestion des tâches pour ${user.name}`)}>
                    <IoEyeSharp />
                  </button>
                  <button className='iconButton' onClick={() => selectTask(user._id)}>
                    <IoAddCircle />
                  </button>
                </td>
              </tr>
          ))}
          </tbody>
        </table>
      </div>
  );
};

export default ListUsers;
