import { FC, useState, useEffect } from 'react';
import User from "../types/User";
import { getTasks, addTask, updateTaskById, deleteTaskById, getTaskById } from '../services/user.service';
import Modal from './Modal';
import UserForm from './UserForm';
import UserTaskList from './UserTasksList';
import EditUserForm from './EditUserForm';

interface ListUserProps {
  title: string;
}

const ListUsers: FC<ListUserProps> = ({ title }: ListUserProps) => {
  const [users, setUsers] = useState<User[] | null>([]);
  const [isOpenForm, setOpenForm] = useState(false);
  const [isOpenEditForm, setOpenEditForm] = useState(false);
  const [userToEdit, setUserToEdit] = useState<User | null>(null);
  const [refresh, setRefresh] = useState(0);

  useEffect(() => {
    const getData = async () => {
      const users: User[] = await getUsers();
      console.log(users);
      setUsers(users);
    }
    getData();
  }, [refresh]);

  const sendNewUser = (formData: any) => {
    setOpenForm(false);
    const add = async (userAdd : User)=>{
      const user = await addUser(userAdd);
      setRefresh(refresh+1);
    }
    add(formData);
  }

  const handleEditClick = (user: User) => {
    setUserToEdit(user);
    setOpenEditForm(true);
  }

  const handleEditSubmit = async (formData: User) => {
    if (userToEdit) {
      const updatedUser = await updateUser(userToEdit._id, formData);
      setRefresh(refresh + 1);
    }
    setOpenEditForm(false);
  }

  return (
      <div>
        <h3>{title}</h3>
        <div>
          <button onClick={() => setOpenForm(true)}>Ajouter un utilisateur</button>
          <Modal
              isOpen={isOpenForm}
              onClose={() => setOpenForm(false)}
              title="Enregistrer un utilisateur"
              content={<UserForm onSubmit={sendNewUser} />}
          />
        </div>
        <table className='center'>
          <thead>
          <tr>
            <th>Nom</th>
            <th>Email</th>
            <th>Tâches associées</th>
            <th>Actions</th>
          </tr>
          </thead>
          <tbody>
          {users?.map((val, key) => {
            return (
                <tr key={key}>
                  <td>{val.name}</td>
                  <td>{val.email}</td>
                  <td>
                    <div>
                      <UserTaskList userId={val._id} />
                    </div>
                  </td>
                  <td>
                    <button onClick={() => handleEditClick(val)}>Edit</button>
                  </td>
                </tr>
            )
          })}
          </tbody>
        </table>
        <Modal
            isOpen={isOpenEditForm}
            onClose={() => setOpenEditForm(false)}
            title="Modifier l'utilisateur"
            content={<EditUserForm onSubmit={handleEditSubmit} userToEdit={userToEdit} />}
        />
      </div>
  )
}

export default ListUsers;
