import { FC, useState, useEffect } from 'react';
import User from "../types/User";
import { getUsers, getUserById, addUser, updateUserById, deleteUserById } from '../services/user.service';
import Modal from './Modal';
import UserForm from './UserForm';
import UserTaskList from './UserTasksList';
import YesOrNo from './YesOrNo';

interface ListUserProps {
  title: string;
}

const ListUsers: FC<ListUserProps> = ({ title }: ListUserProps) => {
  const [users, setUsers] = useState<User[] | null>([]);
  const [isOpenForm, setOpenForm] = useState(false);
  const [refresh, setRefresh] = useState(0);
  const [userToDelete, setUserToDelete] = useState<User | null>(null);
  const [isOpenDeleteConfirmation, setOpenDeleteConfirmation] = useState(false);

  useEffect(() => {
    const getData = async () => {
      const users: User[] = await getUsers();
      console.log(users);
      setUsers(users);
    }
    getData();
  }, [refresh]);

  const sendNewUser = (formData: any) => {
    setOpenForm(false);
    const add = async (userAdd: User) => {
      const user = await addUser(userAdd);
      setRefresh(refresh+1);
    }
    add(formData);
  }

  const handleDeleteUser = async (user: User) => {
    setUserToDelete(user);
    setOpenDeleteConfirmation(true);
  }

  const handleDeleteConfirmation = async (confirmed: boolean) => {
    setOpenDeleteConfirmation(false);
    if (confirmed && userToDelete) {
      await deleteUserById(userToDelete._id);
      setRefresh(refresh + 1);
    }
    setUserToDelete(null);
  }

  return (
      <div>
        <h3>{title}</h3>
        <div>
          <button onClick={() => setOpenForm(true)}>Ajouter un utilisateur</button>
          <Modal
              isOpen={isOpenForm}
              onClose={() => setOpenForm(false)}
              title="Enregistrer un utilisateur"
              content={<UserForm onSubmit={sendNewUser} />}
          />
        </div>
        <table className='center'>
          <thead>
          <tr>
            <th>Nom</th>
            <th>Email</th>
            <th>Tâches associées</th>
            <th>Actions</th>
          </tr>
          </thead>
          <tbody>
          {users?.map((val, key) => {
            return (
                <tr key={key}>
                  <td>{val.name}</td>
                  <td>{val.email}</td>
                  <td>
                    <div>
                      <UserTaskList userId={val._id} />
                    </div>
                  </td>
                  <td>
                    <button onClick={() => handleDeleteUser(val)}>Supprimer</button>
                  </td>
                </tr>
            )
          })}
          </tbody>
        </table>
        {userToDelete && (
            <YesOrNo
                isOpen={isOpenDeleteConfirmation}
                onClose={handleDeleteConfirmation}
                question={`Voulez-vous vraiment supprimer l'utilisateur ${userToDelete.name} ?`}
            />
        )}
      </div>
  )
}

export default ListUsers;
