import React, { useState, useEffect } from "react";
import Task from "../types/Task";
import { getTasks, updateTaskById } from "../services/task.service";

interface FormProps {
    userId: string;
}

interface FormData {
    taskId: string;
}

const UserForm = ({ userId }: FormProps) => {
    const [tasks, setTasks] = useState<Task[]>([]);
    const [formData, setFormData] = useState<FormData>({
        taskId: "",
    });

    useEffect(() => {
        const fetchData = async () => {
            const tasks = await getTasks();
            setTasks(tasks);
        };
        fetchData();
    }, []);

    const handleChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
        const { value } = event.target;
        setFormData((prevFormData) => ({ ...prevFormData, taskId: value }));
    };

    const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        const task = tasks.find((t) => t._id === formData.taskId);
        if (task) {
            const updatedTask = { ...task, userId };
            await updateTaskById(updatedTask._id, updatedTask);
            setFormData({ taskId: "" });
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Tâche :
                <select value={formData.taskId} onChange={handleChange}>
                    <option value="">-- Sélectionnez une tâche --</option>
                    {tasks.map((task) => (
                        <option key={task._id} value={task._id}>
                            {task.title}
                        </option>
                    ))}
                </select>
            </label>
            <button type="submit" disabled={!formData.taskId}>
                Ajouter à la tâche
            </button>
        </form>
    );
};

export default UserForm;
