import React, { useEffect, useState } from 'react';
import { getTasks } from '../services/task.service';
import { getUsers } from '../services/user.service';
import Task from '../types/Task';
import User from '../types/User';

interface Props {
    userId: string;
}

const UserTaskList: React.FC<Props> = ({ userId }) => {
    const [user, setUser] = useState<User | null>(null);
    const [tasks, setTasks] = useState<Task[]>([]);

    useEffect(() => {
        const fetchUser = async () => {
            const users = await getUsers();
            const currentUser = users.find((user: { id: string; }) => user.id === userId);
            setUser(currentUser);
        };
        fetchUser();
    }, [userId]);

    useEffect(() => {
        const fetchTasks = async () => {
            const allTasks = await getTasks();
            const userTasks = allTasks.filter((task: { userId: string; }) => task.userId === userId);
            setTasks(userTasks);
        };
        fetchTasks();
    }, [userId]);

    return (
        <div>
            {tasks.length === 0 && <p>Aucune tâche associée</p>}
            {tasks.length > 0 && (
                <ul className='userTask-list'>
                    {tasks.map((task) => (
                        <li key={task._id}>{task.title}</li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default UserTaskList;
