import { FC, useState, useEffect } from 'react';
import User from "../types/User";
import Task from "../types/Task";
import { getUsers, addUser } from '../services/user.service';
import { getTasks } from '../services/task.service';
import Modal from './Modal';
import UserForm from './UserForm';
import TaskList from './TaskList';
import {IoEyeSharp, IoAddCircle} from 'react-icons/io5';

interface ListUserProps {
  title: string;
}

const ListUsers: FC<ListUserProps> = ({ title }: ListUserProps) => {
  const [users, setUsers] = useState<User[] | null>([]);
  const [tasks, setTasks] = useState<Task[] | null>([]);
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);
  const [isOpenForm, setOpenForm] = useState(false);
  const [refresh, setRefresh] = useState(0);

  useEffect(() => {
    const getUsersAndTasks = async () => {
      const users: User[] = await getUsers();
      setUsers(users);
      const tasks: Task[] = await getTasks();
      setTasks(tasks);
    };
    getUsersAndTasks();
  }, [refresh]);

  const sendNewUser = (formData: any) => {
    setOpenForm(false);
    const add = async (userAdd: User) => {
      const user = await addUser(userAdd);
      setRefresh(refresh + 1);
    };
    add(formData);
  };

  const handleTaskSelect = (task: Task) => {
    setSelectedTask(task);
  };

  return (
      <div>
        <h3>{title}</h3>
        <div>
          <button onClick={() => setOpenForm(true)}>Ajouter un utilisateur</button>
          <Modal
              isOpen={isOpenForm}
              onClose={() => setOpenForm(false)}
              title="Enregistrer un utilisateur"
              content={
                selectedTask ? (
                    <UserForm onSubmit={sendNewUser} />
                ) : (
                    <TaskList tasks={tasks} onTaskSelect={handleTaskSelect} />
                )
              }
          />
        </div>
        <table className='center'>
          <thead>
          <tr>
            <th>Nom</th>
            <th>Email</th>
            <th>Gestion des t√¢ches</th>
          </tr>
          </thead>
          <tbody>
          {users?.map((val, key) => {
            return (
                <tr key={key}>
                  <td>{val.name}</td>
                  <td>{val.email}</td>
                  <td>
                    <button className='iconButton'><IoEyeSharp /></button>
                    <button className='iconButton' onClick={() => setOpenForm(true)}>
                      <IoAddCircle />
                    </button>
                  </td>
                </tr>
            )
          })}
          </tbody>
        </table>
      </div>
  );
};

export default ListUsers;
