import { FC, useState, useEffect } from 'react';
import User from "../types/User";
import { getUsers, addUser, getTasks } from '../services/user.service';
import Modal from './Modal';
import UserForm from './UserForm';
import {IoEyeSharp, IoAddCircle} from 'react-icons/io5';
interface ListUserProps {
  title: string;
}

const ListUsers: FC<ListUserProps> = ({ title }: ListUserProps) => {
  const [users, setUsers] = useState<User[] | null>([]);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [isOpenForm, setOpenForm] = useState(false);
  const [isOpenTasks, setOpenTasks] = useState(false);
  const [selectedTask, setSelectedTask] = useState<string | null>(null);
  const [refresh, setRefresh] = useState(0);

  useEffect(() => {

    const getData = async () => {

      const users: User[] = await getUsers();
      console.log(users);
      setUsers(users);
    }
    getData();
  }, [refresh]);

  const openTaskModal = async (user: User) => {
    setSelectedUser(user);
    setOpenTasks(true);
  };

  const sendNewUser = (formData: any) => {
    if (selectedUser && selectedTask) {
      const user = { ...selectedUser, task: selectedTask };
      const add = async (userAdd : User)=>{
        const user = await addUser(userAdd);
        setRefresh(refresh+1);
      }

      add(user);
    }
    setOpenForm(false);
    setOpenTasks(false);
  }

  return (
      <div>
        <h3>{title}</h3>

        <div>
          <button onClick={() => setOpenForm(true)}>Ajouter un utilisateur</button>
          <Modal
              isOpen={isOpenForm}
              onClose={() => setOpenForm(false)}
              title="Enregistrer un utilisateur"
              content={<UserForm onSubmit={sendNewUser} />}
          />

          <Modal
              isOpen={isOpenTasks}
              onClose={() => setOpenTasks(false)}
              title="Choisir une tâche"
              content={
                <ul>
                  {getTasks().map((task, index) => (
                      <li key={index}>
                        <button onClick={() => setSelectedTask(task)}>{task}</button>
                      </li>
                  ))}
                </ul>
              }
          />
        </div>
        <table className='center'>
          <thead>
          <tr>
            <th>Nom</th>
            <th>Email</th>
            <th>Gestion des tâches</th>
          </tr>
          </thead>
          <tbody>
          {users?.map((val, key) => {
            return (
                <tr key={key}>
                  <td>{val.name}</td>
                  <td>{val.email}</td>
                  <td>
                    <button className='iconButton' onClick={() => alert('Show tasks') }>
                      <IoEyeSharp />
                    </button>
                    <button className='iconButton' onClick={() => openTaskModal(val)}>
                      <IoAddCircle />
                    </button>
                  </td>
                </tr>
            )
          })}
          </tbody>
        </table>
      </div>
  );
};

export default ListUsers;
