import {FC, useEffect, useState} from 'react';
import Task from "../types/Task";
import User from "../types/User";
import {addTask, deleteTaskById, getTasks} from '../services/task.service';
import {getUsers} from '../services/user.service';
import Modal from './Modal';
import TaskForm from './TaskForm';

interface ListTaskProps {
    title: string;
}

const ListTasks: FC<ListTaskProps> = ({title}: ListTaskProps) => {
    const [tasks, setTasks] = useState<Task[] | null>([]);
    const [users, setUsers] = useState<User[] | null>([]);
    const onclick = () => {
        alert('click button')
    };
    const [isOpenForm, setOpenForm] = useState(false);
    const [refresh, setRefresh] = useState(0);

    useEffect(() => {
        const getTasksData = async () => {
            const tasks: Task[] = await getTasks();
            setTasks(tasks);
        }
        getTasksData();

        const getUsersData = async () => {
            const users: User[] = await getUsers();
            setUsers(users);
        }
        getUsersData();
    }, [refresh]);

    const sendNewTask = (formData: any) => {
        setOpenForm(false);
        const add = async (taskAdd: Task) => {
            taskAdd.statut = "En cours"
            const task = await addTask(taskAdd);
            setRefresh(refresh + 1);
        }
        add(formData);
    }

    const handleDeleteTask = async (id: string) => {
        if (window.confirm("Êtes-vous sûr de vouloir supprimer cette tâche ?")) {
            await deleteTaskById(id);
            setRefresh(refresh + 1);
        }
    }

    const getUserNameById = (userId: string | undefined) => {
        if (userId != null) {
            const user = users?.find(user => user._id === userId);
            return user ? user.name : '-';
        }
        return '';
    }

    const handleUpdateStatus = async (taskId, newStatus) => {
        const updatedTask = { ...updatedTasks.find(task => task._id === taskId), statut: newStatus };
        await updateTaskById(taskId, updatedTask);
        setUpdatedTasks(prevTasks => prevTasks.map(task => task._id === taskId ? updatedTask : task));
    };

    return (
        <div>
            <h3>{title}</h3>

            <div>
                <button onClick={() => setOpenForm(true)}>Ajouter une tâche</button>
            </div>

            <table className='center'>
                <thead>
                <tr>
                    <th>Titre</th>
                    <th>Description</th>
                    <th>Statut</th>
                    <th>Utilisateur associé</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {updatedTasks?.map((val, key) => {
                    return (
                        <tr key={key}>
                            <td>{val.title}</td>
                            <td>{val.description}</td>
                            <td>
                                <select value={val.statut} onChange={e => handleUpdateStatus(val._id, e.target.value)}>
                                    <option value="En cours">En cours</option>
                                    <option value="Terminée">Terminée</option>
                                </select>
                            </td>
                            <td>{getUserNameById(val.userId)}</td>
                            <td>
                                <button onClick={() => handleDeleteTask(val._id)}>Delete</button>
                            </td>
                        </tr>
                    )
                })}
                </tbody>
            </table>
        </div>
    )
}

export default ListTasks;
