import { FC, useState } from 'react';
import Task from '../types/Task';
import { addTask } from '../services/task.service';

interface UserAddTaskProps {
    userId: string;
    onClose: () => void;
    onAdd: (task: Task) => void;
}

const UserAddTask: FC<UserAddTaskProps> = ({ userId, onClose, onAdd }: UserAddTaskProps) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');

    const handleAddTask = async () => {
        const task = { title, description, userId, _id: '' };
        const addedTask = await addTask(task);
        onAdd(addedTask);
        onClose();
    };

    return (
        <div>
            <h2>Ajouter une t√¢che</h2>
            <form>
                <label>
                    Titre:
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </label>
                <br />
                <label>
                    Description:
                    <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
                </label>
                <br />
                <button type="button" onClick={handleAddTask}>Ajouter</button>
                <button type="button" onClick={onClose}>Annuler</button>
            </form>
        </div>
    );
};

export default UserAddTask;
