import { FC, useState, useEffect } from 'react';
import User from "../types/User";
import Task from "../types/Task";
import { getUsers, addUser } from '../services/user.service';
import { getTasks } from '../services/task.service';
import Modal from './Modal';
import UserForm from './UserForm';
import {IoEyeSharp, IoAddCircle} from 'react-icons/io5';

interface ListUserProps {
  title: string;
}

const ListUsers: FC<ListUserProps> = ({ title }: ListUserProps) => {
  const [users, setUsers] = useState<User[] | null>([]);
  const [tasks, setTasks] = useState<Task[] | null>([]);
  const [isOpenForm, setOpenForm] = useState(false);
  const [refresh, setRefresh] = useState(0);
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  useEffect(() => {
    const getData = async () => {
      const users: User[] = await getUsers();
      const tasks: Task[] = await getTasks();
      setUsers(users);
      setTasks(tasks);
    }
    getData();
  }, [refresh]);

  const sendNewUser = (formData: any) => {
    setOpenForm(false);
    const addUserAndTask = async (user: User, task: Task) => {
      const addedUser = await addUser(user);
      if (addedUser && task) {
        // Associate the selected task with the newly added user
        const updatedUser = { ...addedUser, taskId: task._id };
        const response = await fetch(`/api/users/${addedUser.id}`, {
          method: 'PUT',
          body: JSON.stringify(updatedUser),
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (!response.ok) {
          throw new Error(`Failed to associate task with user. ${response.statusText}`);
        }
      }
      setRefresh(refresh + 1);
    };

    if (selectedTask) {
      addUserAndTask(formData, selectedTask);
    } else {
      addUser(formData);
    }
  };

  const handleTaskSelection = (task: Task) => {
    setSelectedTask(task);
  }

  return (
      <div>
        <h3>{title}</h3>

        <div>
          <button onClick={() => setOpenForm(true)}>Ajouter un utilisateur</button>
          <Modal
              isOpen={isOpenForm}
              onClose={() => setOpenForm(false)}
              title="Enregistrer un utilisateur"
              content={
                <div>
                  <UserForm onSubmit={sendNewUser} />
                  <h4>Choisir une tâche</h4>
                  {tasks &&
                      <ul>
                        {tasks.map((task, index) => {
                          return (
                              <li key={index} onClick={() => handleTaskSelection(task)}>
                                {task.title}
                              </li>
                          )
                        })}
                      </ul>
                  }
                </div>
              }
          />

        </div>
        <table className='center'>
          <thead>
          <tr>
            <th>Nom</th>
            <th>Email</th>
            <th>Tâche</th>
            <th>Gestion des tâches</th>
          </tr>
          </thead>
          <tbody>
          {users?.map((val, key) => {
            return (
                <tr key={key}>
                  <td>{val.name}</td>
                  <td>{val.email}</td>
                  <td>{val._id}</td>
                <td><button className='iconButton'><IoEyeSharp /></button><button className='iconButton'><IoAddCircle /></button></td>

              </tr>
            )
          })}
        </tbody>
      </table>

    </div>



  )
}

export default ListUsers
